import React from 'react';
import styles from './photopost.module.css';
import Input from '../../../../Components/Form/Input';
import Button from '../../../../Components/Form/Button';
import useForm from '../../../../Hooks/useForm';
import useFetch from '../../../../Hooks/useFetch';
import { PHOTO_POST } from '../../../../api';

function PhotoPost() {
  const nome = useForm();
  const peso = useForm('number');
  const idade = useForm('number');
  const [img, setImg] = React.useState({});
  const { request, error, loading, data } = useFetch();

  function handleImgChange({ target }) {
    setImg({
      raw: target.files[0],
    });
  }
  async function handleSubmit(e) {
    e.preventDefault();
    const formData = new FormData();

    formData.append('img', img.raw);
    formData.append('nome', nome.value);
    formData.append('peso', peso.raw);
    formData.append('idade', idade.raw);

    const token = window.localStorage.getItem('token');
    const { url, options } = PHOTO_POST(formData, token);

    await request(url, options);
  }

  return (
    <section className={`${styles.photoPost} animeLeft`}>
      <form onSubmit={handleSubmit}>
        <Input {...nome} label="Nome" type="text" name="nome" />
        <Input {...peso} label="Peso" type="number" name="peso" />
        <Input {...idade} label="Idade" type="number" name="idade" />
        <input onChange={handleImgChange} label="img" type="file" name="img" />
        <Button>Enviar</Button>
      </form>
    </section>
  );
}

export default PhotoPost;
