import React from 'react';

const useMedia = (media) => {
  const [isMobile, setMobile] = React.useState(true);

  window.addEventListener('resize', () => {
    const { matches } = window.matchMedia(`(max-width:${media}px)`);
    setMobile(matches);
  });

  return {
    isMobile,
  };
};

export default useMedia;
