import React from 'react';

const validate = {
  email: {
    regex: /^[^\s@]+@[^\s@]+\.[^\s@]+$/,
    message: 'Preencha um email válido',
  },
};

function useForm() {
  const [value, setValue] = React.useState('');

  function onChange({ target }) {
    setValue(target.value);
  }

  return {
    value,
    onChange,
  };
}

export default useForm;
