import React from 'react';
import { Link } from 'react-router-dom';
import Button from '../../../../Components/Form/Button';
import Input from '../../../../Components/Form/Input';
import useForm from '../../../../Hooks/useForm';
import styles from './loginForm.module.css';

function LoginForm() {
  const username = useForm('email');
  const password = useForm(false);

  function handleSubmit(e) {
    e.preventDefault();
    if (!username.validate()) return false;
    alert();
    const data = {
      username: username.value,
      password: password.value,
    };
    const options = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    };
    fetch('https://dogsapi.origamid.dev/json/jwt-auth/v1/token', options)
      .then((resp) => {
        resp.json().then((json) => {
          console.log(json);
        });
      })
      .catch((err) => {
        console.error(err);
      });
  }
  return (
    <div>
      <h1>Login</h1>
      <form onSubmit={handleSubmit}>
        <Input name="username" type="text" label="UsuÃ¡rio" {...username} />
        <Input name="password" type="password" label="Senha" {...password} />
        <Button>Entrar</Button>
      </form>
      <Link to="/login/criar">Cadastro</Link>
    </div>
  );
}

export default LoginForm;
