import React from 'react';

const useMedia = (media) => {
  const [match, setMatch] = React.useState(true);

  window.addEventListener('resize', () => {
    const { matches } = window.matchMedia(media);
    setMobile(matches);
  });

  return {
    match,
  };
};

export default useMedia;
