import React from 'react';

const useMedia = () => {
  const [match, setMatch] = React.useState(true);

  window.addEventListener('resize', () => {
    const { matches } = window.matchMedia('(max-width:1000px)');
  });

  return {
    match,
  };
};

export default useMedia;
